INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38938, 0, 0, 'Don\'t talk back to me, troll.  You know who was left in charge here.  Haven\'t you stopped to ask yourself why Thrall chose me instead of you?', 12, 0, 100, 25, 0, 0, 0, 0, 'Vision of Garrosh Hellscream to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38938, 1, 0, 'You are lucky that I don\'t gut you right here, whelp.  You are foolish to think that you can speak to your Warchief in such ways.', 12, 0, 100, 5, 0, 0, 0, 0, 'Vision of Garrosh Hellscream to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38938, 2, 0, 'And what exactly do you think that you are going to do about it?  Your threats are hollow.  Go slink away with the rest of your kind to the slums, I will endure your filth in my throne room no longer.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vision of Garrosh Hellscream to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38938, 3, 0, 'You have sealed your fate, troll.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vision of Garrosh Hellscream to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38938, 4, 0, '%s spits at Vol\'jin\'s feet.', 16, 0, 100, 0, 0, 0, 0, 0, 'Vision of Garrosh Hellscream to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38953, 0, 0, 'Dere be no question why, Garrosh.  He gave ya tha title because ya be Grom\'s son and because tha people be wantin\' a war hero.', 12, 0, 100, 5, 0, 0, 0, 0, 'Vision of Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38953, 1, 0, 'I tink ya be even more like ya father den ya thought, even witout da demon blood.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vision of Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38953, 2, 0, 'Ya be no Warchief of mine.  Ya not earned my respect and I\'ll not be seein\' tha Horde destroyed by ya foolish thirst for war.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vision of Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38953, 3, 0, 'I know exactly what I\'ll be doin\' about it, son of Hellscream.  I\'ll watch and wait as ya people slowly become aware of ya ineptitude.  I\'ll laugh as dey grow ta despise ya as I do.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vision of Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38953, 4, 0, 'And when tha time comes dat ya failure is complete and ya "power" is meaningless, I will be dere to end ya rule swiftly and silently.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vision of Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38953, 5, 0, 'Ya will spend ya reign glancin\' over ya shoulda and fearin\' tha shadows, for when tha time comes and ya blood be slowly drainin\' out, ya will know exactly who fired tha arrow dat pierced ya black heart.\n', 12, 0, 100, 1, 0, 0, 0, 0, 'Vision of Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38953, 6, 0, 'And you yours, "Warchief."', 12, 0, 100, 1, 0, 0, 0, 0, 'Vision of Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 0, 0, 'I have sometin\' to show ya. It be easier to understand if ya see it yourself.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 1, 0, 'Tha Darkspear are \'ere because I led dem here.  Orgrimmar be no home as long as it be under Hellscream\'s hand. ', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 2, 0, 'Still, I fear I was lettin\' my temper drive me ta bein\' rash. Thrall devoted himself to makin\' the Horde what it is, so I\'ve no eagerness to be leavin\' it on a whim.  Dis will be needin\' much more thought.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 3, 0, 'But dese be worries for older minds.  Ya still have much to learn.  Go help tha people of tha Darkspear.  I am sure we\'ll be speakin\' again real soon.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 4, 0, 'Tha Sea Witch is dead, our home is reclaimed, but our future still be uncertain.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 5, 0, 'I\'ve no love for Garrosh, dat much is sure, but leavin\' tha Horde is not a decision I be takin\' lightly.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 6, 0, 'Dere\'s only one with tha answers I seek. Ya can stay and watch if ya like.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 7, 0, 'Thrall!  I am glad ya be well.  Dere were rumors otherwise.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 8, 0, 'I must beg ya council, my friend.  I can\'t be standin\' by Garrosh while he be turnin\' our people against each other for tha sake of war.  My respect for you does not extend to dis new Horde... I am tinkin\' of leadin\' my people away.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 9, 0, 'I understand, brotha. I will tink on this and be troublin\' ya no furtha. You have a world to be savin\'.', 12, 0, 100, 6, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 10, 0, 'Ya be strong and proud, youngblood.  Tha Darkspear will be honored ta have you fight beside d\'em.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 11, 0, 'Dese be dark times, but our people will be stayin\' with tha Horde for tha good of all.  ', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 12, 0, 'Thrall\'s words be true. Dey always be. The Horde is much more den a few old, stubborn leaders and a handful of heroes from Northrend. The people be cryin\' Garrosh\'s name... at least for now.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 13, 0, 'Still, I be hopin\' Thrall will return to us one day. Tha future right now be lookin\' very grim... and very bloody.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
INSERT INTO `creature_text` (`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38966, 14, 0, 'Go now. Make tha Darkspear proud. Dere are many wars ahead of us, an\' I\'m sure ya be havin\' a part to play in all of dem.', 12, 0, 100, 1, 0, 0, 0, 0, 'Vol\'jin to Player');
